Console.WriteLine("Hello, World!");
var input = "";
Queue<char> frame = new();
foreach (char letter in input) {
    if(frame.Count == 4) frame.Dequeue();
    frame.Enqueue(letter);
    if(frame.Count == 4 && frame.Distinct().Count() ==frame.Count) 
    {
        var marker = string.Join("",frame);
        Console.WriteLine($"at pos {input.IndexOf(marker)+marker.Count()} marker {marker}");
        break;
    }
}
foreach (char letter in input) {
    if(frame.Count == 14) frame.Dequeue();
    frame.Enqueue(letter);
